mapper.update:
update	problem_info
set problem = #{problem}, 
	category_seq = #{category_seq}
where problem_seq = #{problem_seq}

mapper.delete:
update problem_info
set delete_yn = 'Y'
where problem_seq = #{problem_seq}

mapper.update:
update	category_info
set category_name = #{category_name}
where category_seq = #{category_seq}

mapper.delete:
update category_info
set delete_yn = 'Y'
where category_seq = #{category_seq}
